import { Component } from 'react';
import { Canvas } from '../canvas/canvas.component';

export class Game extends Component
{
    constructor()
    {
        super();
        this.state = {
          score_player_1: 0,
          score_player_2: 0,
          player_1_position: 50,
          player_2_position: 0
        }

    }

    drawBall = (context, x, y) => {
        context.fillStyle = "white";
        context.fillRect(x, y, 20, 20);
    }
    drawPeddle = (context, x, y) => {
        context.fillStyle = "white";
        context.fillRect(x, y, 20,  80);
    }

    drawLine = (context, width, height) => {

        context.strokeStyle = "white";
        context.setLineDash([15, 5]);
        context.moveTo(width / 2, 0);
        context.lineTo(width / 2, height + 5);
        context.stroke();  
    }

    drawScore = (context, width, height) => {
        context.font = "100px Mina";
        context.fillStyle = "white";
        context.fillText(this.state.score_player_1,  width / 4 , height / 4);
        context.fillText(this.state.score_player_2,  width * 3 / 4 , height / 4);
    }

    draw = (context, width, height, player_1_position) => {
    context.fillStyle = "black";
    context.fillRect(0, 0, width,  height);
    context.beginPath();
    this.drawBall(context, 200, 200);
    this.drawLine(context, width, height);
    this.drawPeddle(context, 10, player_1_position);
    this.drawPeddle(context, width - 30, 150);
    this.drawScore(context, width, height);



  };

    // componentWillMount() {
    //     window.addEventListener('keydown', this.handleKeyboardInput.bind(this));
    // }  
    // handleKeyboardInput = (e) => {
    //     const code = e.keyCode ? e.keyCode : e.which;

    //     if (code === 38) { //up key
    //         this.setState({ keyboardInput: {'Y', -1 }});
    //     }
    // }
    render()
    {

        return(
            <div className='game'>
                <h1>Game</h1>
                <Canvas draw={this.draw} height={500} width={900} player_1_position/>
            </div>
        );
    }
}
