import { Component } from 'react';
// import { Game } from './components/game/game.component';
import './App.css';

var Ball = {
  position_x: 50,
  position_y: 50,
  vel_x: 2,
  vel_y: 2

}
class Peddle {
  constructor(x, y) {
    this.position_x = x;
    this.position_y = y;
    this.size = [10, 50];
  }

}
let ball = Ball;
let peddleOne = new Peddle(10, 50);
let peddleTwo  = new Peddle(580, 150);
let ctx = null;
document.addEventListener("keydown", (event) => {
  peddleOne.position_y = peddleOne.position_y + 10;
});


class Game extends Component {

  componentDidMount() {
    console.log('CDM');
    // let canvas =  document.createElement('canvas');
    let canvas = document.getElementById('forCanvas');
    ctx = canvas.getContext('2d');
    let width = 600;
    let height = 400;


    ctx.fillRect(ball.position_x, ball.position_y, 10 , 10);
    ctx.beginPath();

    window.setInterval(function(){
    // console.log("HEHE");
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, width,  height);
    if (ball.position_x + 5 > width || ball.position_x < 0)
      ball.vel_x = -ball.vel_x;
    if (ball.position_y + 5 > height || ball.position_y < 0)
      ball.vel_y = -ball.vel_y;

    ball.position_x = ball.position_x + ball.vel_x;
    ball.position_y = ball.position_y + ball.vel_y;
    ctx.fillStyle = "white";
    ctx.fillRect(ball.position_x, ball.position_y, 10 , 10);
    ctx.fillRect(peddleOne.position_x, peddleOne.position_y, peddleOne.size[0], peddleOne.size[1])
    ctx.fillRect(peddleTwo.position_x, peddleTwo.position_y, peddleTwo.size[0], peddleTwo.size[1])

  }, 10);

  }

  render() {
    console.log('render');
    return(
      <div>
        <canvas id="forCanvas" width={600} height={400}></canvas>
      </div>
    );
  }
}

class App extends Component
{
  // constructor()  {

  // }

  render(){

    return (
    <div className="App">
      <h1>Pong</h1>
        <Game/>
  </div>
  );
  }
}



export default App;
